import { Card, FormControl, InputGroup } from "react-bootstrap"
import StatusLine from "../task/StatusLine";
import "../../styles/Components/TaskScreen.scss";

export const NewProyect = () => {
    const [tasks, setTasks] = useState([]);

    function addTask(taskToAdd) {
        let filteredTasks = tasks.filter((task) => {
          return task.id !== taskToAdd.id;
        });

        let newTaskList = [...filteredTasks, taskToAdd];

        setTasks(newTaskList);

        //saveTasksToLocalStorage(newTaskList);
      }

      function deleteTask(taskId) {
        let filteredTasks = tasks.filter((task) => {
          return task.id !== taskId;
        });

        setTasks(filteredTasks);

        //saveTasksToLocalStorage(filteredTasks);
      }

      function moveTask(id, newStatus) {
        let task = tasks.filter((task) => {
          return task.id === id;
        })[0];

        let filteredTasks = tasks.filter((task) => {
          return task.id !== id;
        });

        task.status = newStatus;

        let newTaskList = [...filteredTasks, task];

        setTasks(newTaskList);

        //saveTasksToLocalStorage(newTaskList);
      }

    return (
        <div align="center">
            <Card
                bg="dark"
                text="white"
            >
                <Card.Header as="h5">Nuevo Proyecto</Card.Header>
                <Card.Body>
                    <Card.Title>
                        <InputGroup className="mb-3" style={{width: 400}}>
                            <InputGroup.Text>Nombre del Proyecto</InputGroup.Text>
                            <FormControl aria-label="First name" />
                        </InputGroup>
                    </Card.Title>
                    <Card.Text>
                        <div className="row">
                            <div className="col-4">
                            </div>
                            <div className="col-4">

                            </div>
                            <div className="col-4">
                            </div>
                        </div>
                    </Card.Text>
                    <button className='btn btn-prime btn-block'>
                        Prueba
                    </button>
                </Card.Body>
            </Card>
        </div>
    )
}
