import { useState } from "react";
import StatusLine from "../task/StatusLine";
import '../../styles/Components/equip.scss'
import Axios from "axios";
import validator from 'validator'

function Equip(nameproy) {
  const [tasks, setTasks] = useState([]);


  function addEmptyTask(status) {
    const lastTask = tasks[tasks.length - 1];

    let newTaskId = 1;

    if (lastTask !== undefined) {
      newTaskId = lastTask.id + 1;
    }

    setTasks((tasks) => [
      ...tasks,
      {
        id: newTaskId,
        title: "",
        status: status
      }
    ]);
  }

  function addTask(taskToAdd) {
    let filteredTasks = tasks.filter((task) => {
      return task.id !== taskToAdd.id;
    });

    let newTaskList = [...filteredTasks, taskToAdd];
    let prueba = [taskToAdd];
    console.log(prueba[0].title);


    if (!validator.isEmail(prueba[0].title)) {
      if (prueba[0].status == 'Programer') {
        Axios.post("http://localhost:9000/equipo/add/", {
          Nombre: nameproy,
          Cargo: 2,
          Correo: prueba[0].title

        })
          .then(() => {

          })
          .catch(err => {
            Swal.fire('Error', 'Usuario existente o datos incorrectos', 'error')
          })
      } else {

      }
    } else {

    }



    setTasks(newTaskList);
  }




  return (
    <div className="App">
      <h1>Task Management</h1>
      <div className="mb-5">
        <div className="row">
          <div className="col-4">
            <StatusLine
              tasks={tasks}
              addEmptyTask={addEmptyTask}
              addTask={addTask}
              status="Programer"
            />
          </div>
          <div className="col-4">
            <StatusLine
              tasks={tasks}
              addEmptyTask={addEmptyTask}
              addTask={addTask}
              status="Designers"
            />
          </div>
          <div className="col-4">
            <StatusLine
              tasks={tasks}
              addEmptyTask={addEmptyTask}
              addTask={addTask}
              status="Analist"
            />
          </div>
        </div>
        <div className="row">
          <div className="col-4">
            <StatusLine
              tasks={tasks}
              addEmptyTask={addEmptyTask}
              addTask={addTask}
              status="Boss"
            />
          </div>
          <div className="col-4">

          </div>
          <div className="col-4">
            <StatusLine
              tasks={tasks}
              addEmptyTask={addEmptyTask}
              addTask={addTask}
              status="Tester"
            />
          </div>
        </div>
      </div>

    </div>
  );
}

export default Equip;
